import React, { useCallback, useEffect, useMemo, useState } from 'react';
import ReactFlow, {
  addEdge,
  Background,
  Controls,
  MiniMap,
  useEdgesState,
  useNodesState,
  Handle,
  Position,
} from 'reactflow';
import NodeInspector from './components/NodeInspector';
import { saveChain, loadChain, simulate } from './api';

const initialNodes = [
  {
    id: 'event1',
    type: 'event',
    position: { x: 0, y: 0 },
    data: { label: 'Contact Added', eventType: 'contact_added', listName: 'Newsletter' },
  },
  {
    id: 'action1',
    type: 'action',
    position: { x: 250, y: 0 },
    data: { label: 'Wait 1 day', actionType: 'wait', delayValue: 1, delayUnit: 'days' },
  },
  {
    id: 'cond1',
    type: 'condition',
    position: { x: 500, y: -100 },
    data: { label: 'Date > 2025-08-20', conditionType: 'date', operator: '>', value: '2025-08-20' },
  },
  {
    id: 'action2',
    type: 'action',
    position: { x: 750, y: -150 },
    data: { label: 'Send Email', actionType: 'send_email', subject: 'Hello!', body: 'Welcome to our list.' },
  },
  {
    id: 'stop1',
    type: 'action',
    position: { x: 1000, y: -150 },
    data: { label: 'Stop', actionType: 'stop' },
  },
  {
    id: 'stop2',
    type: 'action',
    position: { x: 750, y: 0 },
    data: { label: 'Stop', actionType: 'stop' },
  },
];

const initialEdges = [
  { id: 'e1-2', source: 'event1', target: 'action1' },
  { id: 'e2-3', source: 'action1', target: 'cond1' },
  { id: 'e3-4', source: 'cond1', target: 'action2', label: 'yes', data: { path: 'yes' } },
  { id: 'e4-5', source: 'action2', target: 'stop1' },
  { id: 'e3-6', source: 'cond1', target: 'stop2', label: 'no', data: { path: 'no' } },
];

function nodeStyleForType(nodeType) {
  if (nodeType === 'event') return { borderColor: '#0ea5e9', background: '#e0f2fe' };
  if (nodeType === 'condition') return { borderColor: '#f59e0b', background: '#fff7ed' };
  return { borderColor: '#10b981', background: '#ecfdf5' };
}

function DefaultNode({ data, nodeType }) {
  const style = nodeStyleForType(nodeType);
  return (
    <div style={{
      padding: 8,
      border: '1px solid ' + style.borderColor,
      background: style.background,
      borderRadius: 6,
      minWidth: 140,
      textAlign: 'center',
      fontSize: 12,
    }}>
      <Handle type="target" position={Position.Left} />
      <div style={{ fontWeight: 600 }}>{data?.label || nodeType}</div>
      <div style={{ color: '#666', marginTop: 4 }}>
        {nodeType === 'event' && (data?.eventType || '')}
        {nodeType === 'action' && (data?.actionType || '')}
        {nodeType === 'condition' && (data?.conditionType || '')}
      </div>
      {nodeType === 'condition' ? (
        <>
          <Handle type="source" id="yes" position={Position.Right} style={{ top: '30%' }} />
          <Handle type="source" id="no" position={Position.Right} style={{ top: '70%' }} />
        </>
      ) : (
        <Handle type="source" position={Position.Right} />
      )}
    </div>
  );
}

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selected, setSelected] = useState(null);
  const [logs, setLogs] = useState([]);
  const [now, setNow] = useState(() => new Date().toISOString());
  const [contextInputs, setContextInputs] = useState({ emailOpened: false, emailClicked: false });

  const nodeTypes = useMemo(() => ({
    event: (props) => <DefaultNode {...props} nodeType="event" />,
    action: (props) => <DefaultNode {...props} nodeType="action" />,
    condition: (props) => <DefaultNode {...props} nodeType="condition" />,
  }), []);

  const onConnect = useCallback((params) => {
    setEdges((eds) => {
      const sourceNode = nodes.find((n) => n.id === params.source);
      const newEdge = { ...params, id: `e-${Math.random().toString(36).slice(2, 8)}` };
      if (sourceNode?.type === 'condition') {
        const path = params.sourceHandle === 'yes' ? 'yes' : 'no';
        newEdge.data = { ...(newEdge.data || {}), path };
        newEdge.label = path;
      }
      return addEdge(newEdge, eds);
    });
  }, [setEdges, nodes]);

  const onNodeClick = useCallback((_e, node) => {
    setSelected(node);
  }, []);

  const onNodeUpdate = (updatedNode) => {
    setNodes((nds) => nds.map((n) => (n.id === updatedNode.id ? updatedNode : n)));
    setSelected(updatedNode);
  };

  const addNode = (type) => {
    const id = `${type}-${Math.random().toString(36).slice(2, 7)}`;
    const base = { id, type, position: { x: 50, y: 200 }, data: { label: type } };
    if (type === 'event') base.data = { ...base.data, eventType: 'contact_added' };
    if (type === 'action') base.data = { ...base.data, actionType: 'wait', delayValue: 1, delayUnit: 'days' };
    if (type === 'condition') base.data = { ...base.data, conditionType: 'date', operator: '>', value: '' };
    setNodes((nds) => nds.concat(base));
  };

  const handleSave = async () => {
    await saveChain({ nodes, edges });
    alert('Saved');
  };

  const handleLoad = async () => {
    const chain = await loadChain();
    if (chain) {
      setNodes(chain.nodes);
      setEdges(chain.edges);
      setSelected(null);
    } else {
      alert('No saved chain found');
    }
  };

  const handleSimulate = async () => {
    const trigger = { type: 'contact_added', payload: { listName: 'Newsletter', email: 'user@example.com' } };
    const result = await simulate({ nodes, edges }, trigger, { now, ...contextInputs });
    setLogs(result.logs || []);
    if (result.context?.now) setNow(result.context.now);
  };

  useEffect(() => {
    // Ensure a clean selection if node removed
    if (selected && !nodes.find((n) => n.id === selected.id)) {
      setSelected(null);
    }
  }, [nodes, selected]);

  return (
    <div style={{ display: 'flex', height: '100vh', width: '100vw' }}>
      <div style={{ width: 260, borderRight: '1px solid #e5e7eb', padding: 12 }}>
        <h2 style={{ marginTop: 0 }}>Email Chain Builder</h2>
        <div style={{ display: 'flex', gap: 8, marginBottom: 8 }}>
          <button onClick={() => addNode('event')}>+ Event</button>
          <button onClick={() => addNode('action')}>+ Action</button>
          <button onClick={() => addNode('condition')}>+ Condition</button>
        </div>

        <div style={{ display: 'flex', gap: 8, marginBottom: 8 }}>
          <button onClick={handleSave}>Save</button>
          <button onClick={handleLoad}>Load</button>
        </div>

        <div style={{ marginBottom: 12 }}>
          <div style={{ fontWeight: 600, marginBottom: 4 }}>Simulation Context</div>
          <div style={{ marginBottom: 6 }}>
            <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Current Time (ISO)</label>
            <input style={{ width: '100%' }} value={now} onChange={(e) => setNow(e.target.value)} />
          </div>
          <div style={{ display: 'flex', gap: 8 }}>
            <label style={{ fontSize: 12 }}>
              <input
                type="checkbox"
                checked={contextInputs.emailOpened}
                onChange={(e) => setContextInputs((c) => ({ ...c, emailOpened: e.target.checked }))}
              /> Email Opened
            </label>
            <label style={{ fontSize: 12 }}>
              <input
                type="checkbox"
                checked={contextInputs.emailClicked}
                onChange={(e) => setContextInputs((c) => ({ ...c, emailClicked: e.target.checked }))}
              /> Clicked
            </label>
          </div>
        </div>

        <button onClick={handleSimulate} style={{ width: '100%', marginBottom: 12 }}>Simulate</button>

        <div>
          <div style={{ fontWeight: 600, marginBottom: 4 }}>Logs</div>
          <div style={{ background: '#f8fafc', border: '1px solid #e5e7eb', padding: 8, height: 220, overflow: 'auto' }}>
            {(logs || []).map((l, i) => (
              <div key={i} style={{ fontFamily: 'monospace', fontSize: 12, marginBottom: 4 }}>
                {l}
              </div>
            ))}
          </div>
        </div>
      </div>

      <div style={{ flex: 1, display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onNodeClick={onNodeClick}
            nodeTypes={nodeTypes}
            fitView
          >
            <MiniMap />
            <Controls />
            <Background gap={12} size={1} />
          </ReactFlow>
        </div>
        <div style={{ width: 320, borderLeft: '1px solid #e5e7eb' }}>
          <NodeInspector node={selected} onChange={onNodeUpdate} />
        </div>
      </div>
    </div>
  );
}
