import React, { useEffect, useMemo, useState } from 'react';

function stepIcon(text) {
  const t = (text || '').toLowerCase();
  if (t.includes('start at event')) return 'üö©';
  if (t.includes('waited') || t.includes('wait')) return '‚è≥';
  if (t.includes('sent email') || t.includes('send_email') || t.includes('email')) return '‚úâÔ∏è';
  if (t.includes('condition')) return 'üîé';
  if (t.includes('stopped')) return 'üõë';
  return '‚û°Ô∏è';
}

export default function SimulationModal({ open, steps, userMessages, onClose }) {
  const safeSteps = useMemo(() => Array.isArray(steps) ? steps : [], [steps]);
  const safeUserMessages = useMemo(() => Array.isArray(userMessages) ? userMessages : [], [userMessages]);
  const [index, setIndex] = useState(0);

  useEffect(() => {
    if (!open) return;
    setIndex(0);
  }, [open]);

  useEffect(() => {
    if (!open) return;
    if (index >= safeSteps.length - 1) return;
    const id = setTimeout(() => setIndex((i) => Math.min(i + 1, safeSteps.length - 1)), 900);
    return () => clearTimeout(id);
  }, [index, open, safeSteps.length]);

  if (!open) return null;

  return (
    <div className="ui-modal-overlay" onClick={onClose}>
      <div className="ui-modal" onClick={(e) => e.stopPropagation()}>
        <div className="ui-modal-header">
          <div className="ui-title" style={{ fontSize: 20 }}>–°–∏–º—É–ª—è—Ü–∏—è</div>
          <button className="ui-button" onClick={onClose}>–ó–∞–∫—Ä—ã—Ç—å</button>
        </div>
        <div className="ui-modal-stage">
          {safeSteps.slice(0, index + 1).map((s, i) => (
            <div key={i} className="ui-step">
              <span className="ui-step-icon">{stepIcon(s)}</span>
              <span className="ui-step-text">{s}</span>
            </div>
          ))}
          {safeSteps.length === 0 && (
            <div className="ui-step">–ù–µ—Ç —à–∞–≥–æ–≤</div>
          )}
        </div>
      </div>
    </div>
  );
}
