import React from 'react';

export default function NodeInspector({ node, onChange }) {
  if (!node) return <div style={{ padding: 12, color: '#666' }}>Select a node to edit.</div>;

  const isEvent = node.type === 'event';
  const isAction = node.type === 'action';
  const isCondition = node.type === 'condition';

  const set = (key, value) => {
    onChange({ ...node, data: { ...node.data, [key]: value } });
  };

  return (
    <div style={{ padding: 12 }}>
      <h3 style={{ marginTop: 0 }}>Node Inspector</h3>
      <div style={{ marginBottom: 8 }}>
        <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Label</label>
        <input
          value={node.data?.label || ''}
          onChange={(e) => set('label', e.target.value)}
          placeholder="Display label"
          style={{ width: '100%' }}
        />
      </div>

      {isEvent && (
        <div>
          <div style={{ marginBottom: 8 }}>
            <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Event Type</label>
            <select
              value={node.data?.eventType || 'contact_added'}
              onChange={(e) => set('eventType', e.target.value)}
              style={{ width: '100%' }}
            >
              <option value="contact_added">Contact Added</option>
              <option value="date">Specific Date</option>
            </select>
          </div>
          {node.data?.eventType === 'contact_added' && (
            <div style={{ marginBottom: 8 }}>
              <label style={{ display: 'block', fontSize: 12, color: '#666' }}>List Name (optional)</label>
              <input
                value={node.data?.listName || ''}
                onChange={(e) => set('listName', e.target.value)}
                placeholder="Newsletter Subscribers"
                style={{ width: '100%' }}
              />
            </div>
          )}
          {node.data?.eventType === 'date' && (
            <div style={{ marginBottom: 8 }}>
              <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Date</label>
              <input
                type="date"
                value={node.data?.date || ''}
                onChange={(e) => set('date', e.target.value)}
                style={{ width: '100%' }}
              />
            </div>
          )}
        </div>
      )}

      {isAction && (
        <div>
          <div style={{ marginBottom: 8 }}>
            <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Action Type</label>
            <select
              value={node.data?.actionType || 'wait'}
              onChange={(e) => set('actionType', e.target.value)}
              style={{ width: '100%' }}
            >
              <option value="wait">Wait</option>
              <option value="send_email">Send Email</option>
              <option value="stop">Stop Chain</option>
            </select>
          </div>
          {node.data?.actionType === 'wait' && (
            <div style={{ display: 'flex', gap: 8, marginBottom: 8 }}>
              <div style={{ flex: 1 }}>
                <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Delay</label>
                <input
                  type="number"
                  min="0"
                  value={node.data?.delayValue || 1}
                  onChange={(e) => set('delayValue', e.target.value)}
                  style={{ width: '100%' }}
                />
              </div>
              <div>
                <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Unit</label>
                <select
                  value={node.data?.delayUnit || 'days'}
                  onChange={(e) => set('delayUnit', e.target.value)}
                >
                  <option value="days">days</option>
                  <option value="hours">hours</option>
                </select>
              </div>
            </div>
          )}
          {node.data?.actionType === 'send_email' && (
            <div>
              <div style={{ marginBottom: 8 }}>
                <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Subject</label>
                <input
                  value={node.data?.subject || ''}
                  onChange={(e) => set('subject', e.target.value)}
                  placeholder="Welcome!"
                  style={{ width: '100%' }}
                />
              </div>
              <div style={{ marginBottom: 8 }}>
                <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Body</label>
                <textarea
                  value={node.data?.body || ''}
                  onChange={(e) => set('body', e.target.value)}
                  placeholder="Hi there..."
                  style={{ width: '100%', minHeight: 100 }}
                />
              </div>
            </div>
          )}
        </div>
      )}

      {isCondition && (
        <div>
          <div style={{ marginBottom: 8 }}>
            <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Condition Type</label>
            <select
              value={node.data?.conditionType || 'date'}
              onChange={(e) => set('conditionType', e.target.value)}
              style={{ width: '100%' }}
            >
              <option value="date">Date</option>
              <option value="time">Time</option>
              <option value="opened">Email Opened (mock)</option>
              <option value="clicked">Clicked (mock)</option>
            </select>
          </div>

          <div style={{ marginBottom: 8 }}>
            <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Operator</label>
            <select
              value={node.data?.operator || '>'}
              onChange={(e) => set('operator', e.target.value)}
              style={{ width: '100%' }}
            >
              <option value=">">&gt;</option>
              <option value=">=">&gt;=</option>
              <option value="<">&lt;</option>
              <option value="<=">&lt;=</option>
              <option value="=">=</option>
            </select>
          </div>

          {['date'].includes(node.data?.conditionType) && (
            <div style={{ marginBottom: 8 }}>
              <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Date</label>
              <input
                type="date"
                value={node.data?.value || ''}
                onChange={(e) => set('value', e.target.value)}
                style={{ width: '100%' }}
              />
            </div>
          )}
          {node.data?.conditionType === 'time' && (
            <div style={{ marginBottom: 8 }}>
              <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Time (HH:MM)</label>
              <input
                type="time"
                value={node.data?.value || ''}
                onChange={(e) => set('value', e.target.value)}
                style={{ width: '100%' }}
              />
            </div>
          )}
          {['opened', 'clicked'].includes(node.data?.conditionType) && (
            <div style={{ marginBottom: 8 }}>
              <label style={{ display: 'block', fontSize: 12, color: '#666' }}>Expected</label>
              <select
                value={String(node.data?.value || 'false')}
                onChange={(e) => set('value', e.target.value)}
                style={{ width: '100%' }}
              >
                <option value="true">true</option>
                <option value="false">false</option>
              </select>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
